from django.db import models#class DivisionModel(models.Model):#    Id = models#    Name = models.CharField(max_length=100)    #    class Meta:#        db_table="Division"    #def __str__(self):    #    return self.Nameclass DepartmentInsert(models.Model):    Id = models.IntegerField()    Dept_name = models.CharField(max_length=100)    create_by = models.CharField(max_length=100)    create_date = models.DateField(max_length=100)    updated_by = models.CharField(max_length=100)    updated_date = models.DateField(max_length=100)    pc_name = models.CharField(max_length=100)    Is_Active = models.BooleanField(max_length=100)    class Meta:        db_table="department"class DesignationInsert(models.Model):    Id = models.IntegerField()    Designation = models.CharField(max_length=100)    create_by = models.CharField(max_length=100)    create_date = models.DateField(max_length=100)    updatedby = models.CharField(max_length=100)    updated_date= models.DateField(max_length=100)    pc_name = models.CharField(max_length=100)    class Meta:        db_table="designation"class LocationInsert(models.Model):    Id = models.IntegerField()    location = models.CharField(max_length=100)    create_by = models.CharField(max_length=100)    create_date = models.DateField(max_length=100)    updatedby = models.CharField(max_length=100)    updated_date= models.DateField(max_length=100)    pc_name = models.CharField(max_length=100)    class Meta:        db_table="location"class EmployeeRegModel(models.Model):    Id = models.IntegerField()    Employee_code = models.CharField(max_length=500)    Staff_Type = models.CharField(max_length=500)    User_Name = models.CharField(max_length=500)    Employee_Title =  models.CharField(max_length=100)    Employee_name =  models.CharField(max_length=500)    Father_Husb_Title = models.CharField(max_length=500)    Father_Husb_name = models.CharField(max_length=500)    Mother_name = models.CharField(max_length=500)    Employee_cnic = models.CharField(max_length=15)    Father_cnic = models.CharField(max_length=15)    Contact = models.CharField(max_length=15)    Other_contact = models.CharField(max_length=15)    Gender = models.CharField(max_length=100)    Joining_date = models.DateField(max_length=500)    Staff_cat_id = models.IntegerField(max_length=500)    Department_id =  models.IntegerField(max_length=500)    Location_id = models.IntegerField(max_length=500)    Employee_type_id = models.IntegerField(max_length=500)    Employment_type_id = models.IntegerField(max_length=500)    Salary_Grade_id = models.IntegerField(max_length=500)    Designation_id = models.IntegerField(max_length=500)    Report_Direct = models.IntegerField(max_length=500)    Report_InDirect = models.IntegerField(max_length=500)    Create_by = models.CharField(max_length=500)    Create_date = models.DateField(max_length=500)    Updated_by = models.CharField(max_length=500)    Updated_date =models.DateField(max_length=500)    IsActive = models.BooleanField(max_length=500)    Pc_name = models.CharField(max_length=500)    class Meta:        db_table="employee_reg"